import React from "react";

// Componente MovieList que muestra una lista de películas
const MovieList = () => {
    // Supongamos que tienes un array de películas con información como nombre, puntuación y URL del póster
    const movies = [
        {
            name: "Interstellar",
            category: "Sci-Fi",
            rating: 9.2,
            posterUrl: "https://example.com/interstellar-poster.jpg",
        },
        {
            name: "Inception",
            category: "Sci-Fi",
            rating: 8.8,
            posterUrl: "https://example.com/inception-poster.jpg",
        },
        {
            name: "The Shawshank Redemption",
            category: "Drama",
            rating: 9.3,
            posterUrl: "https://example.com/shawshank-redemption-poster.jpg",
        },
        // Agrega más películas según sea necesario
    ];

    return (
        <div>
            <h2>Lista de Películas</h2>
            <ul>
                {movies.map((movie, index) => (
                    <li key={index}>
                        <img src={movie.posterUrl} alt={movie.name} />
                        <div>
                            <h3>{movie.name}</h3>
                            <p>Categoría: {movie.category}</p>
                            <p>Puntuación: {movie.rating}</p>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MovieList;
