// moviesData.js
const generateYoutubeId = (title) => {
    // Eliminar espacios y caracteres especiales del título para generar el ID
    const formattedTitle = title.replace(/\s+/g, '').toLowerCase();
    return formattedTitle.substring(0, 11); // Tomar los primeros 11 caracteres
  };

  const movies = [
    {
      title: "Star Wars ep3",
      poster: "url_al_poster_1",
      people: ["Persona 1", "Persona 2", "Persona 3"]
    },
    {
      title: "Pelicula 2",
      poster: "url_al_poster_2",
      people: ["Persona 4", "Persona 5", "Persona 6"]
    },
    // Añade más películas si lo deseas
  ];

  // Agregar el youtubeId basado en el título
  movies.forEach(movie => {
    movie.youtubeId = generateYoutubeId(movie.title);
  });

  export default movies;
