import React, { useState, useEffect } from "react";
import movies from "./moviesData";

const MovieCarousel = () => {
    const [currentMovieIndex, setCurrentMovieIndex] = useState(0);
    const [cast, setCast] = useState([]);

    const handleNext = () => {
        setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
    };

    const handlePrev = () => {
        setCurrentMovieIndex((prevIndex) =>
            prevIndex === 0 ? movies.length - 1 : prevIndex - 1
        );
    };

    useEffect(() => {
        const fetchCast = async () => {
            const response = await fetchCastData(); // Replace with your fetch function for cast data
            setCast(response);
        };

        fetchCast();
    }, [currentMovieIndex]);

    const currentMovie = movies[currentMovieIndex];

    return (
        <div className="movie-carousel">
            <div className="movie-details">
                <div className="poster-container">
                    <img
                        className="poster"
                        src={currentMovie.poster}
                        alt="Movie Poster"
                    />
                </div>
                <div className="trailer-container">
                    <iframe
                        className="trailer"
                        width="560"
                        height="315"
                        src={`https://www.youtube.com/embed/${currentMovie.youtubeId}`}
                        title="YouTube video player"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                        allowFullScreen
                    ></iframe>
                </div>
            </div>
            <div className="cast">
                <h3>People Involved</h3>
                <ul>
                    {cast.map((person, index) => (
                        <li key={index}>{person.name}</li>
                    ))}
                </ul>
            </div>
            <div className="navigation-buttons">
                <button className="prev-btn" onClick={handlePrev}>
                    Previous
                </button>
                <button className="next-btn" onClick={handleNext}>
                    Next
                </button>
            </div>
        </div>
    );
};

// Replace fetchCastData with your actual function to fetch cast data based on the current movie index
const fetchCastData = async () => {
    const response = await fetch(
        `https://api.themoviedb.org/3/movie/${movies[currentMovieIndex].id}/credits?api_key=${apiKey}`
    );
    const data = await response.json();
    return data.cast;
};

export default MovieCarousel;
