import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import Footer from "./Footer";
<header className="menu">
            {/* Logo */}
            <img
                src="http://imgfz.com/i/OehyQAa.png"
                alt="Logo"
                className="logo"
            />

            {/* Menú de navegación */}
            <nav> 
            </nav>
        </header>

function App() {
    const API_URL = "https://api.themoviedb.org/3";
    const API_KEY = "674684d28cd5c404ad1bf06cd1a5d482";
    const IMAGE_PATH = "https://image.tmdb.org/t/p/original";
    const URL_IMAGE = "https://image.tmdb.org/t/p/original";

    const [movies, setMovies] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState(null);
    const [searchKey, setSearchKey] = useState("");
    const [trailer, setTrailer] = useState(null);
    const [movie, setMovie] = useState({ title: "Loading Movies" });
    const [playing, setPlaying] = useState(false);

    const handleSearch = (query) => {
        setSearchKey(query);
    };

    const fetchMovies = async (searchKey) => {
        const type = searchKey ? "search" : "discover";
        const {
            data: { results },
        } = await axios.get(`${API_URL}/${type}/movie`, {
            params: {
                api_key: API_KEY,
                query: searchKey,
            },
        });

        const filteredMovies = results.filter(
            (movie) =>
                !movie.title.toLowerCase().includes("tiburon") &&
                movie.title !== "No Way Up" &&
                movie.title !== "Jaws" &&
                movie.title !== "Sharknado" &&
                movie.title !== "Deep Blue Sea" &&
                movie.title !== "The Meg" &&
                movie.title !== "Shark"
        );

        setMovies(filteredMovies);
        setMovie(filteredMovies[0]);

        if (filteredMovies.length) {
            await fetchMovie(filteredMovies[0].id);
        }
    };

    const fetchMovie = async (id) => {
        const { data } = await axios.get(`${API_URL}/movie/${id}`, {
            params: {
                api_key: API_KEY,
                append_to_response: "videos",
            },
        });

        if (data.videos && data.videos.results) {
            const trailer = data.videos.results.find(
                (vid) => vid.name === "Official Trailer"
            );
            setTrailer(trailer ? trailer : data.videos.results[0]);
        }
        setMovie(data);
    };

    const selectMovie = async (movie) => {
        fetchMovie(movie.id);
        setMovie(movie);
        window.scrollTo(0, 0);
    };

    const searchMovies = (e) => {
        e.preventDefault();
        fetchMovies(searchKey);
    };

    useEffect(() => {
        fetchMovies();
    }, []);

    return (
        <div className="App">
            <Header handleSearch={handleSearch} setMovies={setMovies} />

            <form className="container mb-4" onSubmit={searchMovies}>
                <input
                    type="text"
                    placeholder="search"
                    onChange={(e) => setSearchKey(e.target.value)}
                />
                <button className="btn btn-primary">Search</button>
            </form>

            <div>
                <main>
                    {movie ? (
                        <div
                            className="viewtrailer"
                            style={{
                                backgroundImage: `url("${IMAGE_PATH}${movie.backdrop_path}")`,
                            }}
                        >
                            {playing ? (
                                <button
                                    onClick={() => setPlaying(false)}
                                    className="boton"
                                >
                                    Close
                                </button>
                            ) : (
                                <div className="container">
                                    <div className="">
                                        {trailer ? (
                                            <button
                                                className="boton"
                                                onClick={() => setPlaying(true)}
                                                type="button"
                                            >
                                                Play Trailer
                                            </button>
                                        ) : (
                                            "Sorry, no trailer available"
                                        )}
                                        <h1 className="text-white">
                                            {movie.title}
                                        </h1>
                                        <p className="text-white">
                                            {movie.overview}
                                        </p>
                                    </div>
                                </div>
                            )}
                        </div>
                    ) : null}
                </main>
            </div>

            <div className="container mt-3">
                <div className="row">
                    {movies.map((movie) => (
                        <div
                            key={movie.id}
                            className="col-md-4 mb-3"
                            onClick={() => selectMovie(movie)}
                        >
                            <img
                                src={`${URL_IMAGE + movie.poster_path}`}
                                alt=""
                                height={600}
                                width="100%"
                            />
                            <h4 className="text-center">{movie.title}</h4>
                        </div>
                    ))}
                </div>
            </div>

            <Footer />
        </div>
    );
}

export default App;
