// moviesData.js
const fetch = require('node-fetch'); // Asegúrate de tener instalado el paquete 'node-fetch'

const MOVIE_POSTER_DB_API_KEY = "234";
const MOVIE_POSTER_DB_API_SECRET = "tS5SPZa4ikob6q5ExKK86NRdvLIqAQLWxj93bFxZ";

const fetchMovieDetails = async (imdbId) => {
  try {
    const response = await fetch(`http://api.movieposterdb.com/search?ImdbMovieId=${imdbId}&ApiKey=${MOVIE_POSTER_DB_API_KEY}&ApiSecret=${MOVIE_POSTER_DB_API_SECRET}`);
    const data = await response.json();
    if (data.Posters && data.Posters.length > 0) {
      const posterUrl = data.Posters[0].Url;
      const title = data.Title;
      return { poster: posterUrl, title: title };
    } else {
      return null;
    }
  } catch (error) {
    console.error("Error fetching movie details:", error);
    return null;
  }
};

const movies = [
  {
    imdbId: "tt1375666", // Ejemplo de ID de IMDb (Inception)
    people: [],
  },
  {
    imdbId: "tt0068646", // Ejemplo de ID de IMDb (The Godfather)
    people: [],
  },
  // Agrega más películas con sus respectivos IDs de IMDb
];

const fillMovieDetails = async () => {
  for (const movie of movies) {
    const movieDetails = await fetchMovieDetails(movie.imdbId);
    if (movieDetails) {
      movie.title = movieDetails.title;
      movie.poster = movieDetails.poster;
    }
  }
};

fillMovieDetails();

export default movies;
