import React, { useState } from "react";
import movies from "./moviesData";

const MovieCarousel = () => {
    const [currentMovieIndex, setCurrentMovieIndex] = useState(0);

    const handleNext = () => {
        setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
    };

    const handlePrev = () => {
        setCurrentMovieIndex((prevIndex) =>
            prevIndex === 0 ? movies.length - 1 : prevIndex - 1
        );
    };

    const currentMovie = movies[currentMovieIndex];

    const renderCast = () => {
        const directors = [];
        const producers = [];
        const actors = [];

        currentMovie.cast.forEach((person) => {
            if (person.role === "Director") {
                directors.push(person);
            } else if (person.role === "Producer") {
                producers.push(person);
            } else {
                actors.push(person);
            }
        });

        return (
            <>
                {directors.map((director, index) => (
                    <li key={index}>{director.name} - Director</li>
                ))}
                {producers.map((producer, index) => (
                    <li key={index}>{producer.name} - Producer</li>
                ))}
                {actors.map((actor, index) => (
                    <li key={index}>{actor.name} - Actor</li>
                ))}
            </>
        );
    };

    return (
        <div className="movie-carousel">
            <div className="movie-details">
                <div className="poster-container">
                    <img
                        className="poster"
                        src={currentMovie.poster}
                        alt="Movie Poster"
                    />
                </div>
                <div className="trailer-container">
                    <iframe
                        className="trailer"
                        width="560"
                        height="315"
                        src={`https://www.youtube.com/embed/${currentMovie.youtubeId}`}
                        title="YouTube video player"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                        allowFullScreen
                    ></iframe>
                </div>
            </div>
            <div className="people-involved">
                <h3>People Involved</h3>
                <ul>{renderCast()}</ul>
            </div>
            <div className="navigation-buttons">
                <button className="prev-btn" onClick={handlePrev}>
                    Previous
                </button>
                <button className="next-btn" onClick={handleNext}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default MovieCarousel;
