// moviesData.js
const TMDB_API_KEY = "AIzaSyBOti4mM-6x9WDnZIjIeyEU21OpBXqWBgw"; // Clave de API pública de TMDb (solo para desarrollo)

const fetchMovieDetails = async (tmdbId) => {
    try {
        const response = await fetch(
            `https://api.themoviedb.org/3/movie/${tmdbId}?api_key=${TMDB_API_KEY}&language=es`
        );
        const data = await response.json();
        return data;
    } catch (error) {
        console.error("Error fetching movie details:", error);
        return null;
    }
};

const movies = [
    {
        tmdbId: 550, // Ejemplo de ID de película en TMDb (The Dark Knight)
        people: [],
    },
    {
        tmdbId: 238, // Ejemplo de ID de película en TMDb (The Godfather)
        people: [],
    },
    // Agrega más películas con sus respectivos IDs de TMDb
];

const fillMovieDetails = async () => {
    for (const movie of movies) {
        const movieDetails = await fetchMovieDetails(movie.tmdbId);
        if (movieDetails) {
            movie.title = movieDetails.title;
            movie.poster = `https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`;
            // Se puede obtener más información, como el reparto, desde movieDetails.credits.cast
        }
    }
};

fillMovieDetails();

export default movies;
