        youtubeLink: "https://www.youtube.com/watch?v=8ugaeA-nMTc",
        people: ["Persona 7", "Persona 8", "Persona 9"],
        rating: "8.5",
        saga: "Marvel",
        category: "Super",
    },
    {
        title: "Black Panther",
        poster: "https://images-na.ssl-images-amazon.com/images/I/81F2-RuT5tL._AC_SY606_.jpg",
        youtubeLink: "https://www.youtube.com/watch?v=xjDjIWPwcPU",
        people: ["Persona 10", "Persona 11", "Persona 12"],
        rating: "9.0",
        saga: "Marvel",
        category: "Super",
    },
];

// Agrupar películas por saga y categoría
const moviesBySagaAndCategory = movies.reduce((acc, movie) => {
    const { saga, category } = movie;
    if (!acc[saga]) {
        acc[saga] = {};
    }
    if (!acc[saga][category]) {
        acc[saga][category] = [];
    }
    acc[saga][category].push(movie);
    return acc;
}, {});

export { moviesBySagaAndCategory };

movies.forEach((movie) => {
    movie.youtubeId = extractYoutubeId(movie.youtubeLink);
});

export default movies;
