// moviesData.js
const fetchMoviePoster = async (title, apiKey) => {
    try {
        const response = await fetch(
            `https://api.movieposterdb.com/posters?title=${title}&api_key=${apiKey}`
        );
        const data = await response.json();
        if (data && data.length > 0) {
            return data[0].image_url;
        } else {
            return null;
        }
    } catch (error) {
        console.error("Error fetching movie poster:", error);
        return null;
    }
};

const movies = [
    {
        title: "Pelicula 1",
        youtubeLink: "https://www.youtube.com/watch?v=qIYyXcCwvKc",
        people: [],
    },
    {
        title: "Pelicula 2",
        youtubeLink: "https://www.youtube.com/watch?v=abcdefghijk",
        people: [],
    },
    // Añade más películas si lo deseas
];

const fillMovieDetails = async (apiKey) => {
    for (const movie of movies) {
        const poster = await fetchMoviePoster(movie.title, apiKey);
        if (poster) {
            movie.poster = poster;
        }
    }
};

fillMovieDetails("234|tS5SPZa4ikob6q5ExKK86NRdvLIqAQLWxj93bFxZ");

export default movies;
