import React from 'react';
import './StarRating.css';

const StarRating = ({ rating }) => {
    // Calcula el número máximo de estrellas a mostrar (5)
    const maxStars = 5;

    // Convierte la calificación de una escala de 10 a una de 5
    const scaledRating = (rating / 2);

    // Calcula el número de estrellas llenas y medias
    const fullStars = Math.min(Math.floor(scaledRating), maxStars);  // No más de 5 estrellas llenas
    const halfStar = scaledRating % 1 >= 0.5 && fullStars < maxStars;  // Media estrella solo si hay espacio y es necesario

    const stars = [];

    // Construye el arreglo de estrellas
    for (let i = 0; i < maxStars; i++) {
        if (i < fullStars) {
            stars.push(<span key={i} className="star">&#9733;</span>); // Estrella llena
        } else if (i === fullStars && halfStar) {
            stars.push(<span key={i} className="star">&#9733;&#9734;</span>); // Media estrella
        } else {
            stars.push(<span key={i} className="star">&#9734;</span>); // Estrella vacía
        }
    }

    return <div className="star-rating">{stars}</div>;
};

export default StarRating;
