import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import axios from "axios";
import "./App.css";
import MovieCarousel from "./MovieCarousel";
import Footer from "./Footer";
import MovieList from "./MovieList";
import MovieDetail from "./MovieDetail";

// CSS
import "./MovieList.css";
import "./MovieDetail.css";
import "./Logocss.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./MovieCarousel.css";

const API_URL = "https://api.themoviedb.org/3";
const API_KEY = "674684d28cd5c404ad1bf06cd1a5d482";
const IMAGE_PATH = "https://image.tmdb.org/t/p/original";
const URL_IMAGE = "https://image.tmdb.org/t/p/original";

function App() {
    const [movies, setMovies] = useState([]);
    const [searchKey, setSearchKey] = useState("");
    const [trailer, setTrailer] = useState(null);
    const [movie, setMovie] = useState({ title: "Loading Movies" });
    const [playing, setPlaying] = useState(false);
    const [cast, setCast] = useState([]);
    const [category, setCategory] = useState("");
    const [searchType, setSearchType] = useState("");

    const fetchMovies = async (
        searchKey,
        category,
        searchType,
        fromDate,
        toDate
    ) => {
        const type = searchKey ? "search" : "discover";
        let params = {
            api_key: API_KEY,
            query: searchKey,
            with_genres: category ? category : undefined,
        };

        if (searchType) {
            params = { ...params, ...{ sort_by: searchType } };
        }

        if (fromDate && toDate) {
            params["primary_release_date.gte"] = fromDate;
            params["primary_release_date.lte"] = toDate;
        }

        const {
            data: { results },
        } = await axios.get(`${API_URL}/${type}/movie`, {
            params: params,
        });

        const filteredMovies = results.filter(
            (movie) =>
                !movie.title.toLowerCase().includes("tiburon") &&
                movie.title !== "No Way Up" &&
                movie.title !== "Jaws" &&
                movie.title !== "Sharknado" &&
                movie.title !== "Deep Blue Sea" &&
                movie.title !== "The Meg" &&
                movie.title !== "Shark" &&
                movie.title !== "Under Paris"
        );

        setMovies(filteredMovies);
        setMovie(filteredMovies[0]);

        if (filteredMovies.length) {
            await fetchMovie(filteredMovies[0].id);
        }
    };

    const fetchMovie = async (id) => {
        const { data } = await axios.get(`${API_URL}/movie/${id}`, {
            params: {
                api_key: API_KEY,
                append_to_response: "videos,credits",
            },
        });

        if (data.videos && data.videos.results) {
            const trailer = data.videos.results.find(
                (vid) => vid.name === "Official Trailer"
            );
            setTrailer(trailer ? trailer : data.videos.results[0]);
        }
        setMovie(data);
        if (data.credits && data.credits.cast) {
            setCast(data.credits.cast);
        }
    };

    const selectMovie = async (movie) => {
        fetchMovie(movie.id);
        setMovie(movie);
        window.scrollTo(0, 0);
    };

    const searchMovies = (e) => {
        e.preventDefault();
        fetchMovies(searchKey, category, searchType);
    };

    const goToHomePage = () => {
        window.location.href = "/";
    };

    const handleCategoryChange = (category) => {
        setCategory(category);
        setSearchType("");
        fetchMovies(searchKey, category, searchType);
    };

    useEffect(() => {
        fetchMovies("", "", "");
    }, []);

    const fetchPopularMovies = async () => {
        setSearchType("popularity.desc");
        fetchMovies(searchKey, category, "popularity.desc");
    };

    const fetchRecentMovies = async () => {
        const currentDate = new Date();
        const fromDate = new Date(currentDate);
        const toDate = new Date(currentDate);

        fromDate.setMonth(currentDate.getMonth() - 1);
        toDate.setMonth(currentDate.getMonth() + 1);

        const fromDateString = fromDate.toISOString().split("T")[0];
        const toDateString = toDate.toISOString().split("T")[0];

        setSearchType("release_date.desc");

        fetchMovies(
            searchKey,
            category,
            `release_date.desc`,
            fromDateString,
            toDateString
        );
    };

    const fetchTopRatedMovies = async () => {
        setSearchType("vote_average.desc");
        fetchMovies(searchKey, category, "vote_average.desc");
    };

    const fetchTopRatedMoviesAll = async () => {
        setSearchType("top_rated");
        const {
            data: { results },
        } = await axios.get(`${API_URL}/movie/top_rated`, {
            params: {
                api_key: API_KEY,
            },
        });

        setMovies(results);
        setMovie(results[0]);
        if (results.length) {
            await fetchMovie(results[0].id);
        }
    };

    const categories = {
        28: "Action",
        35: "Comedy",
        18: "Drama",
        27: "Horror",
        10749: "Romance",
        878: "Science Fiction",
        10751: "Family",
        16: "Animation",
        80: "Crime",
        37: "Western",
        36: "History",
        14: "Fantasy",
        53: "Thriller",
        10752: "War",
        12: "Adventure",
        99: "Documentary",
        10402: "Music",
        10770: "TV Movie",
        9648: "Mystery",
    };

    return (
        <Router>
            <div className="App">
                <header className="menu">
                    <div className="logo-and-search">
                        <button className="logo" onClick={goToHomePage}>
                            <img src="http://imgfz.com/i/5hQ3ZSJ.png" alt="Logo" />
                        </button>
                        <form className="container mb-4" onSubmit={searchMovies}>
                            <input
                                type="text"
                                placeholder="search"
                                onChange={(e) => setSearchKey(e.target.value)}
                            />
                            <button className="btn btn-primary" onClick={searchMovies}>
                                Search
                            </button>
                        </form>
                        <div className="category-buttons">
                            <select
                                value={category}
                                onChange={(e) => handleCategoryChange(e.target.value)}
                            >
                                <option value="">All</option>
                                {Object.entries(categories).map(([id, name]) => (
                                    <option key={id} value={id}>
                                        {name}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="category-buttons">
                            <button className="filter-button" onClick={fetchTopRatedMoviesAll}>All time</button>
                            <button className="filter-button" onClick={fetchPopularMovies}>Popular</button>
                            <button className="filter-button" onClick={fetchRecentMovies}>Recent</button>
                            <button className="filter-button" onClick={fetchTopRatedMovies}>Top Rated</button>
                        </div>
                    </div>
                </header>
                <MovieCarousel movies={movies} selectMovie={selectMovie} />
                <main>
                    <Routes>
                        <Route exact path="/" element={
                            movie ? (
                                <div
                                    className="viewtrailer"
                                    style={{
                                        backgroundImage: `url("${IMAGE_PATH}${movie.backdrop_path}")`,
                                    }}
                                >
                                    {trailer && (
                                        <div className="trailer-container">
                                            <img
                                                src={`${IMAGE_PATH}${movie.poster_path}`}
                                                alt={movie.title}
                                                className="movie-poster"
                                                width="560"
                                                height="815"
                                            />
                                            <iframe
                                                width="560"
                                                height="315"
                                                src={`https://www.youtube.com/embed/${trailer.key}`}
                                                title="YouTube video player"
                                                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                                allowFullScreen
                                            ></iframe>
                                        </div>
                                    )}
                                    <div className="cast-container">
                                        <h2>Cast</h2>
                                        <ul>
                                            {cast
                                                .filter((actor) => actor.department === "Directing")
                                                .map((director) => (
                                                    <li key={director.id}>
                                                        Director: {director.name}
                                                    </li>
                                                ))}
                                            {cast
                                                .filter(
                                                    (actor) =>
                                                        actor.order <= 10 &&
                                                        actor.department !== "Directing"
                                                )
                                                .map((actor) => (
                                                    <li key={actor.id}>
                                                        {actor.character} - {actor.name}
                                                    </li>
                                                ))}
                                        </ul>
                                    </div>
                                    {playing ? (
                                        <button onClick={() => setPlaying(false)} className="boton">
                                            Close
                                        </button>
                                    ) : (
                                        <div className="container">
                                            <div>
                                                <h1 className="text-white">{movie.title}</h1>
                                                <p className="text-white">{movie.overview}</p>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            ) : null
                        }/>
                        <Route path="/movies" element={<MovieList movies={movies} selectMovie={selectMovie} />} />
                        <Route path="/movie/:id" element={<MovieDetail movie={movie} trailer={trailer} cast={cast} />} />
                    </Routes>
                </main>
                <Footer />
            </div>
        </Router>
    );
}

export default App;
