import React, { useState, useEffect } from "react";
import { movies } from "./moviesData";

const MovieCard = () => {
    const [randomMovies, setRandomMovies] = useState([]);

    useEffect(() => {
        generateRandomMovies();
    }, []);
    <button onClick={handleClick}>Next Random Movie</button>

    const generateRandomMovies = () => {
        const randomIndexes = [];
        while (randomIndexes.length < 5) {
            const randomIndex = Math.floor(Math.random() * movies.length);
            if (!randomIndexes.includes(randomIndex)) {
                randomIndexes.push(randomIndex);
            }
        }
        const randomMoviesList = randomIndexes.map((index) => movies[index]);
        setRandomMovies(randomMoviesList);
    };

    return (
        <div className="movie-card-container">
            <h2>Random Movie Posters</h2>
            <div className="movie-card-list">
                {randomMovies.map((movie, index) => (
                    <div key={index} className="movie-card">
                        <img
                            src={movie.poster}
                            alt={movie.title}
                            className="movie-poster"
                            onError={(e) => {
                                e.target.onerror = null;
                                e.target.src = "URL_DEL_POSTER_ALTERNATIVO"; // Aquí puedes proporcionar una URL de imagen alternativa o un mensaje de error
                            }}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
};


export default MovieCard;
