import React, { useState, useEffect } from "react";
import movies from "./moviesData";

const MovieCarousel = () => {
    const [currentMovieIndex, setCurrentMovieIndex] = useState(0);
    const [currentDirectors, setCurrentDirectors] = useState([]);
    const [currentCast, setCurrentCast] = useState([]);

    useEffect(() => {
        const fetchMovieDetails = async () => {
            const movie = movies[currentMovieIndex];
            const { directors, cast } = await Dara(movie);
            setCurrentDirectors(directors);
            setCurrentCast(cast);
        };

        fetchMovieDetails();
    }, [currentMovieIndex]);

    const handleNext = () => {
        setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
    };

    const handlePrev = () => {
        setCurrentMovieIndex((prevIndex) =>
            prevIndex === 0 ? movies.length - 1 : prevIndex - 1
        );
    };


    const currentMovie = movies[currentMovieIndex];

    return (
        <div className="movie-carousel">
            <div className="movie-details">
                <div className="poster-container">
                    <img
                        className="poster"
                        src={currentMovie.poster}
                        alt="Movie Poster"
                    />
                </div>

                <div className="trailer-container">
                    <iframe
                        className="trailer"
                        width="560"
                        height="315"
                        src={`https://www.youtube.com/embed/${currentMovie.youtubeId}`}
                        title="YouTube video player"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                        allowFullScreen
                    ></iframe>
                </div>

                <div className="details">
                    <h2>{currentMovie.title}</h2>
                    <h3>Directors:</h3>
                    <ul>
                        {currentDirectors.map((director, index) => (
                            <li key={index}>{director}</li>
                        ))}
                    </ul>
                    <h3>Cast:</h3>
                    <ul>
                        {currentCast.map((actor, index) => (
                            <li key={index}>
                                {actor.name} - {actor.character}
                            </li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="navigation-buttons">
                <button className="prev-btn" onClick={handlePrev}>
                    Previous
                </button>
                <button className="next-btn" onClick={handleNext}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default MovieCarousel;
