// src/App.js
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import './App.css';
import MovieCarousel from './MovieCarousel';
import Footer from './Footer';
import {
  setMovies,
  setMovie,
  setTrailer,
  setCast,
  setSearchKey,
  setCategory,
  setSearchType,
  setPlaying,
} from './actions/movieActions';

const API_URL = "https://api.themoviedb.org/3";
const API_KEY = process.env.REACT_APP_TMDB_API_KEY;
const IMAGE_PATH = "https://image.tmdb.org/t/p/original";
const URL_IMAGE = "https://image.tmdb.org/t/p/original";

function App() {
  const dispatch = useDispatch();
  const {
    movies,
    searchKey,
    trailer,
    movie,
    playing,
    cast,
    category,
    searchType,
  } = useSelector((state) => state.movies);

  const fetchMovies = async (searchKey, category, searchType, fromDate, toDate) => {
    const type = searchKey ? 'search' : 'discover';
    let params = {
      api_key: API_KEY,
      query: searchKey,
      with_genres: category || undefined,
    };

    if (searchType) {
      params = { ...params, sort_by: searchType };
    }

    if (fromDate && toDate) {
      params['primary_release_date.gte'] = fromDate;
      params['primary_release_date.lte'] = toDate;
    }

    try {
      const { data: { results } } = await axios.get(`${API_URL}/${type}/movie`, { params });
      const filteredMovies = results.filter(movie => !['tiburon', 'No Way Up', 'Jaws', 'Sharknado', 'Deep Blue Sea', 'The Meg', 'Shark', 'Under Paris'].includes(movie.title.toLowerCase()));
      dispatch(setMovies(filteredMovies));
      if (filteredMovies.length) {
        fetchMovie(filteredMovies[0].id);
      }
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
  };

  const fetchMovie = async (id) => {
    try {
      const { data } = await axios.get(`${API_URL}/movie/${id}`, {
        params: {
          api_key: API_KEY,
          append_to_response: 'videos,credits',
        },
      });
      const trailer = data.videos?.results.find((vid) => vid.name === 'Official Trailer') || data.videos.results[0];
      dispatch(setTrailer(trailer));
      dispatch(setMovie(data));
      dispatch(setCast(data.credits?.cast || []));
    } catch (error) {
      console.error('Error fetching movie:', error);
    }
  };

  const selectMovie = (movie) => {
    fetchMovie(movie.id);
    dispatch(setMovie(movie));
    window.scrollTo(0, 0);
  };

  const searchMovies = (e) => {
    e.preventDefault();
    fetchMovies(searchKey, category, searchType);
  };

  const goToHomePage = () => {
    window.location.href = '/';
  };

  const handleCategoryChange = (category) => {
    dispatch(setCategory(category));
    dispatch(setSearchType(''));
    fetchMovies(searchKey, category, searchType);
  };

  useEffect(() => {
    fetchMovies('', '', '');
  }, []);

  const fetchPopularMovies = () => {
    dispatch(setSearchType('popularity.desc'));
    fetchMovies(searchKey, category, 'popularity.desc');
  };

  const fetchRecentMovies = () => {
    const currentDate = new Date();
    const fromDate = new Date(currentDate.setMonth(currentDate.getMonth() - 1)).toISOString().split('T')[0];
    const toDate = new Date(currentDate.setMonth(currentDate.getMonth() + 2)).toISOString().split('T')[0];
    dispatch(setSearchType('release_date.desc'));
    fetchMovies(searchKey, category, 'release_date.desc', fromDate, toDate);
  };

  const fetchTopRatedMovies = () => {
    dispatch(setSearchType('vote_average.desc'));
    fetchMovies(searchKey, category, 'vote_average.desc');
  };

  const fetchTopRatedMoviesAll = async () => {
    dispatch(setSearchType('top_rated'));
    try {
      const { data: { results } } = await axios.get(`${API_URL}/movie/top_rated`, { params: { api_key: API_KEY } });
      dispatch(setMovies(results));
      if (results.length) {
        fetchMovie(results[0].id);
      }
    } catch (error) {
      console.error('Error fetching top rated movies:', error);
    }
  };

  const categories = {
    28: 'Action',
    35: 'Comedy',
    18: 'Drama',
    27: 'Horror',
    10749: 'Romance',
    878: 'Science Fiction',
    10751: 'Family',
    16: 'Animation',
    80: 'Crime',
    37: 'Western',
    36: 'History',
    14: 'Fantasy',
    53: 'Thriller',
    10752: 'War',
    12: 'Adventure',
    99: 'Documentary',
    10402: 'Music',
    10770: 'TV Movie',
    9648: 'Mystery',
  };

  return (
    <div className="App">
      <header className="menu">
        <div className="logo-and-search">
          <button className="logo" onClick={goToHomePage}>
            <img src="http://imgfz.com/i/5hQ3ZSJ.png" alt="Logo" />
          </button>
          <form className="container mb-4" onSubmit={searchMovies}>
            <input type="text" placeholder="search" onChange={(e) => dispatch(setSearchKey(e.target.value))} />
            <button className="btn btn-primary" onClick={searchMovies}>
              Search
            </button>
          </form>
          <div className="category-buttons">
            <select value={category} onChange={(e) => handleCategoryChange(e.target.value)}>
              <option value="">All</option>
              {Object.entries(categories).map(([id, name]) => (
                <option key={id} value={id}>
                  {name}
                </option>
              ))}
            </select>
          </div>
          <div className="category-buttons">
            <button className="filter-button" onClick={fetchTopRatedMoviesAll}>All time</button>
            <button className="filter-button" onClick={fetchPopularMovies}>Popular</button>
            <button className="filter-button" onClick={fetchRecentMovies}>Recent</button>
            <button className="filter-button" onClick={fetchTopRatedMovies}>Top Rated</button>
          </div>
        </div>
      </header>
      <div>
        <MovieCarousel movies={movies} selectMovie={selectMovie} />
        <main>
          {movie ? (
            <div className="viewtrailer" style={{ backgroundImage: `url("${IMAGE_PATH}${movie.backdrop_path}")` }}>
              {trailer && (
                <div className="trailer-container">
                  <img src={`${IMAGE_PATH}${movie.poster_path}`} alt={movie.title} className="movie-poster" width="560" height="815" />
                  <iframe
                    width="560"
                    height="315"
                    src={`https://www.youtube.com/embed/${trailer.key}`}
                    title="YouTube video player"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                  ></iframe>
                </div>
              )}
              <div className="viewtrailer">
                <h1>{movie.title}</h1>
                {movie.overview && <p>{movie.overview}</p>}
              </div>
            </div>
          ) : null}
        </main>
      </div>
      <div className="cast">
        {cast.length > 0 && (
          <div className="cast-container">
            <h2>Cast</h2>
            <div className="cast-list">
              {cast.map((actor) => (
                <div key={actor.id} className="cast-member">
                  <img src={`${URL_IMAGE}${actor.profile_path}`} alt={actor.name} width="200" height="300" />
                  <p>{actor.name}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}

export default App;
