import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

import "./App.css";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";


function App() {
    const API_URL = "https://api.themoviedb.org/3";
    const API_KEY = "4f5f43495afcc67e9553f6c684a82f84";
    const IMAGE_PATH = "https://image.tmdb.org/t/p/original";

    // endpoint para las imágenes
    const URL_IMAGE = "https://image.tmdb.org/t/p/original";

    // variables de estado
    const [movies, setMovies] = useState([]);
    const [selectedMovie, setSelectedMovie] = useState(null); // Película seleccionada
    const [searchKey, setSearchKey] = useState("");
    const [trailer, setTrailer] = useState(null);
    const [movie, setMovie] = useState({ title: "Loading Movies" });
    const [playing, setPlaying] = useState(false);

    const handleSearch = (query) => {
        // Aquí puedes implementar la lógica para buscar películas
        console.log("Buscando películas con el query:", query);
    };

    // función para realizar la petición GET a la API
    const fetchMovies = async (searchKey) => {
        const type = searchKey ? "search" : "discover";
        const {
            data: { results },
        } = await axios.get(`${API_URL}/${type}/movie`, {
            params: {
                api_key: API_KEY,
                query: searchKey,
            },
        });

        // Filtrar las películas que contengan la palabra "tiburón" en el título
        const filteredMovies = results.filter(
            (movie) =>
                !movie.title.toLowerCase().includes("tiburon") &&
                movie.title !== "No Way Up" &&
                movie.title !== "Jaws" &&
                movie.title !== "Sharknado" &&
                movie.title !== "Deep Blue Sea" &&
                movie.title !== "The Meg" &&
                movie.title !== "Shark"
        );

        setMovies(filteredMovies);
        setMovie(filteredMovies[0]);

        if (filteredMovies.length) {
            await fetchMovie(filteredMovies[0].id);
        }
    };

    // función para la petición de un solo objeto y mostrarlo en reproductor de videos
    const fetchMovie = async (id) => {
        const { data } = await axios.get(`${API_URL}/movie/${id}`, {
            params: {
                api_key: API_KEY,
                append_to_response: "videos",
            },
        });

        if (data.videos && data.videos.results) {
            const trailer = data.videos.results.find(
                (vid) => vid.name === "Official Trailer"
            );
            setTrailer(trailer ? trailer : data.videos.results[0]);
        }
        setMovie(data);
    };

    const selectMovie = async (movie) => {
        fetchMovie(movie.id);
        setMovie(movie);
        window.scrollTo(0, 0);
    };

    // función para buscar películas
    const searchMovies = (e) => {
        e.preventDefault();
        fetchMovies(searchKey);
    };

    useEffect(() => {
        // eslint-disable-next-line
        fetchMovies(); // eslint-disable-next-line
    }, []); // eslint-disable-next-line
    // eslint-disable-next-line

    return (
      <div className="App">
          <h2 className="text-center mt-5 mb-5">Trailer Popular Movies</h2>

          {/* el buscador */}
          <form className="container mb-4" onSubmit={searchMovies}>
              <input
                  type="text"
                  placeholder="search"
                  onChange={(e) => setSearchKey(e.target.value)}
              />
              <button className="btn btn-primary">Search</button>
          </form>

          {/* contenedor para previsualizar */}
          <div>
              <main>
                  {movie ? (
                      <div
                          className="viewtrailer"
                          style={{
                              backgroundImage: `url("${IMAGE_PATH}${movie.backdrop_path}")`,
                          }}
                      >
                          {playing ? (
                              <>
                                  <button
                                      onClick={() => setPlaying(false)}
                                      className="boton"
                                  >
                                      Close
                                  </button>
                              </>
                          ) : (
                              <div className="container">
                                  <div className="">
                                      {trailer ? (
                                          <button
                                              className="boton"
                                              onClick={() => setPlaying(true)}
                                              type="button"
                                          >
                                              Play Trailer
                                          </button>
                                      ) : (
                                          "Sorry, no trailer available"
                                      )}
                                      <h1 className="text-white">
                                          {movie.title}
                                      </h1>
                                      <p className="text-white">
                                          {movie.overview}
                                      </p>
                                  </div>
                              </div>
                          )}
                      </div>
                  ) : null}
              </main>
          </div>

          {/* contenedor para mostrar los posters y las películas en la petición a la API */}
          <div className="container mt-3">
              <div className="row">
                  {movies.map((movie) => (
                      <div
                          key={movie.id}
                          className="col-md-4 mb-3"
                          onClick={() => selectMovie(movie)}
                      >
                          <img
                              src={`${URL_IMAGE + movie.poster_path}`}
                              alt=""
                              height={600}
                              width="100%"
                          />
                          <h4 className="text-center">{movie.title}</h4>
                      </div>
                  ))}
              </div>
          </div>

          <Header handleSearch={handleSearch} />
          <Main
              movies={movies}
              setMovies={setMovies}
              selectedMovie={selectedMovie}
              setSelectedMovie={setSelectedMovie}
          />
          <Footer />
      </div>
  );

}

export default App;
