import React from 'react';
import Slider from 'react-slick';

function MovieCarousel({ movies, selectMovie }) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 300,
        slidesToShow: 5,
        slidesToScroll: 5,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />
    };

    const CustomPrevArrow = (props) => {
        const { className, onClick } = props;
        return (
            <div className={className} style={{ ...styles.arrow, left: '-30px' }} onClick={onClick}>
                Prev
            </div>
        );
    };

    const CustomNextArrow = (props) => {
        const { className, onClick } = props;
        return (
            <div className={className} style={{ ...styles.arrow, right: '-30px' }} onClick={onClick}>
                Next
            </div>
        );
    };

    return (
        <div style={{ maxWidth: '1300px', margin: '40px auto' }}>
            <Slider {...settings}>
                {movies.map(movie => (
                    <div key={movie.id} onClick={() => selectMovie(movie)} style={{ padding: "5px", textAlign: 'center' }}>
                        <img src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`} alt={movie.title} style={{ maxWidth: "400px", height: "auto" }} />
                    </div>
                ))}
            </Slider>
        </div>
    );
}

const styles = {
    arrow: {
        position: 'absolute',
        top: '50%',
        transform: 'translateY(-50%)',
        cursor: 'pointer',
        zIndex: 1,
        fontSize: '18px',
        color: '#fff',
        background: 'rgba(0,0,0,0.5)',
        padding: '10px',
        borderRadius: '50%',
        width: '40px',
        height: '40px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
    }
};

export default MovieCarousel;
