import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css"; // Archivo de estilos CSS

const App = () => {
    const [movies, setMovies] = useState([]);
    const [currentMovieIndex, setCurrentMovieIndex] = useState(0);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const response = await axios.get(
                    "https://imdb-top-100-movies.p.rapidapi.com/",
                    {
                        headers: {
                            "X-RapidAPI-Key":
                                "8dc9a2dc9cmsh80bb222bb8def6cp156aeejsndcb8e91e1cd7",
                            "X-RapidAPI-Host":
                                "imdb-top-100-movies.p.rapidapi.com",
                        },
                    }
                );
                setMovies(response.data);
            } catch (error) {
                console.error("Error al obtener las películas:", error);
            }
        };

        fetchMovies();
    }, []);

    const handleNext = () => {
        setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
    };

    const handlePrev = () => {
        setCurrentMovieIndex((prevIndex) =>
            prevIndex === 0 ? movies.length - 1 : prevIndex - 1
        );
    };

    if (movies.length === 0) {
        return <div>Loading...</div>;
    }

    const currentMovie = movies[currentMovieIndex];

    return (
        <div className="app">
            <div className="movie-carousel">
                <div className="movie-details">
                    <img
                        className="poster"
                        src={currentMovie.poster}
                        alt="Movie Poster"
                    />
                    <div className="people-involved">
                        <h3>People Involved</h3>
                        <ul>
                            {currentMovie.people.map((person, index) => (
                                <li key={index}>{person}</li>
                            ))}
                        </ul>
                    </div>
                </div>
                <div className="trailer-container">
                    <iframe
                        className="trailer"
                        width="560"
                        height="315"
                        src={`https://www.youtube.com/embed/${currentMovie.youtubeId}`}
                        title="YouTube video player"
                        frameBorder="0"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                        allowFullScreen
                    ></iframe>
                </div>
                <button className="prev-btn" onClick={handlePrev}>
                    Previous
                </button>
                <button className="next-btn" onClick={handleNext}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default App;
