import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
import Footer from "./Footer";

function App() {
    const API_URL = "https://api.themoviedb.org/3";
    const API_KEY = "674684d28cd5c404ad1bf06cd1a5d482";
    const IMAGE_PATH = "https://image.tmdb.org/t/p/original";
    const URL_IMAGE = "https://image.tmdb.org/t/p/original";

    const [movies, setMovies] = useState([]);
    const [searchKey, setSearchKey] = useState("");
    const [trailer, setTrailer] = useState(null);
    const [movie, setMovie] = useState({ title: "Loading Movies" });
    const [playing, setPlaying] = useState(false);
    const [cast, setCast] = useState([]);

    const [showCategories, setShowCategories] = useState(false);
    const [showLanguage, setShowLanguage] = useState(false);

    // Funciones para alternar la visibilidad de las categorías e idioma
    const toggleCategories = () => {
        setShowCategories(!showCategories);
        setShowLanguage(false); // Asegúrate de ocultar el otro menú si está abierto
    };

    const toggleLanguage = () => {
        setShowLanguage(!showLanguage);
        setShowCategories(false); // Asegúrate de ocultar el otro menú si está abierto
    };


    const fetchMovies = async (searchKey) => {
        const type = searchKey ? "search" : "discover";
        const {
            data: { results },
        } = await axios.get(`${API_URL}/${type}/movie`, {
            params: {
                api_key: API_KEY,
                query: searchKey,
            },
        });

        const filteredMovies = results.filter(
            (movie) =>
                !movie.title.toLowerCase().includes("tiburon") &&
                movie.title !== "No Way Up" &&
                movie.title !== "Jaws" &&
                movie.title !== "Sharknado" &&
                movie.title !== "Deep Blue Sea" &&
                movie.title !== "The Meg" &&
                movie.title !== "Shark"
        );

        setMovies(filteredMovies);
        setMovie(filteredMovies[0]);

        if (filteredMovies.length) {
            await fetchMovie(filteredMovies[0].id);
        }
    };

    const fetchMovie = async (id) => {
        const { data } = await axios.get(`${API_URL}/movie/${id}`, {
            params: {
                api_key: API_KEY,
                append_to_response: "videos,credits",
            },
        });

        if (data.videos && data.videos.results) {
            const trailer = data.videos.results.find(
                (vid) => vid.name === "Official Trailer"
            );
            setTrailer(trailer ? trailer : data.videos.results[0]);
        }
        setMovie(data);
        if (data.credits && data.credits.cast) {
            setCast(data.credits.cast);
        }
    };

    const fetchGenres = async () => {
        const { data } = await axios.get(`${API_URL}/genre/movie/list`, {
            params: {
                api_key: API_KEY,
            },
        });
        setGenres(data.genres);
    };

    const fetchLanguages = async () => {
        const { data } = await axios.get(`${API_URL}/configuration/languages`, {
            params: {
                api_key: API_KEY,
            },
        });
        setLanguages(data);
    };

    const selectMovie = async (movie) => {
        fetchMovie(movie.id);
        setMovie(movie);
        window.scrollTo(0, 0);
    };

    const searchMovies = (e) => {
        e.preventDefault();
        fetchMovies(searchKey);
    };

    useEffect(() => {
        fetchMovies();
        fetchGenres();
        fetchLanguages();
    }, []);

    return (
        <div className="App">
            <header className="menu">
                <div className="logo-and-search">
                    <img
                        src="http://imgfz.com/i/OehyQAa.png"
                        alt="Logo"
                        className="logo"
                    />
                    <form className="container mb-4" onSubmit={searchMovies}>
                        <input
                            type="text"
                            placeholder="search"
                            onChange={(e) => setSearchKey(e.target.value)}
                        />
                        <button className="btn btn-primary">Search</button>
                    </form>
                </div>
            </header>

            <div>
                <main>
                    {movie ? (
                        <div
                            className="viewtrailer"
                            style={{
                                backgroundImage: `url("${IMAGE_PATH}${movie.backdrop_path}")`,
                            }}
                        >
                            {/* Agregamos el componente MovieCarousel debajo del Footer */}
                            {trailer && (
                                <div className="trailer-container">
                                    {/* Agregamos el póster de la película */}
                                    <img
                                        src={`${IMAGE_PATH}${movie.poster_path}`}
                                        alt={movie.title}
                                        className="movie-poster"
                                        width="560"
                                        height="815"
                                    />
                                    {/* Agregamos el reproductor de video */}
                                    <iframe
                                        width="560"
                                        height="315"
                                        src={`https://www.youtube.com/embed/${trailer.key}`}
                                        title="YouTube video player"
                                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                        allowFullScreen
                                    ></iframe>
                                </div>
                            )}
                            <div className="cast-container">
                                <h2>Cast</h2>
                                <ul>
                                    {/* Mostrar al director primero */}
                                    {cast
                                        .filter(
                                            (actor) =>
                                                actor.department === "Directing"
                                        ) // Filtrar al director
                                        .map((director) => (
                                            <li key={director.id}>
                                                Director: {director.name}
                                            </li>
                                        ))}
                                    {/* Luego, mostrar a los otros actores principales */}
                                    {cast
                                        .filter(
                                            (actor) =>
                                                actor.order <= 10 &&
                                                actor.department !== "Directing"
                                        ) // Filtrar actores principales (excluyendo al director)
                                        .map((actor) => (
                                            <li key={actor.id}>
                                                {actor.character} - {actor.name}
                                            </li>
                                        ))}
                                </ul>
                            </div>

                            {playing ? (
                                <button
                                    onClick={() => setPlaying(false)}
                                    className="boton"
                                >
                                    Close
                                </button>
                            ) : (
                                <div className="container">
                                    <div className="">
                                        <h1 className="text-white">
                                            {movie.title}
                                        </h1>
                                        <p className="text-white">
                                            {movie.overview}
                                        </p>
                                    </div>
                                </div>
                            )}
                        </div>
                    ) : null}
                </main>
            </div>

            <div className="container mt-3">
                <div className="row">
                    {movies.map((movie) => (
                        <div
                            key={movie.id}
                            className="col-md-4 mb-3"
                            onClick={() => selectMovie(movie)}
                        >
                            <img
                                src={`${URL_IMAGE + movie.poster_path}`}
                                alt=""
                                height={600}
                                width="100%"
                            />
                            <h4 className="text-center">{movie.title}</h4>
                            {/* Agregar el rating aquí */}
                            <p className="text-center">
                                Rating: {movie.vote_average}
                            </p>
                        </div>
                    ))}
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default App;
