// MovieList.js
import React from 'react';
import movies from './MoviesData'; // Importar el array de películas
import './MovieList.css'; // Importar estilos CSS

const MovieList = () => {
  return (
    <div className="movie-list">
      {movies.map((movie, index) => (
        <div className="movie-card" key={index}>
          <img src={movie.poster} alt={movie.title} className="poster" />
          <div className="movie-details">
            <h3>{movie.title}</h3>
            <p><strong>Categoría:</strong> {movie.category}</p>
            <p><strong>Puntuación:</strong> {movie.score}</p>
            <p><strong>Personas:</strong> {movie.people.join(', ')}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default MovieList;
