import React, { useState, useEffect } from "react";
import movies from "./moviesData";

const MovieCarousel = () => {
    const [currentMovieIndex, setCurrentMovieIndex] = useState(0);
    const [currentDirectors, setCurrentDirectors] = useState([]);
    const [currentCast, setCurrentCast] = useState([]);
    const [trailerUrl, setTrailerUrl] = useState("");

    useEffect(() => {
        const fetchMovieDetails = async () => {
            const movie = movies[currentMovieIndex];
            const { directors, cast, trailer } = await getMovieDetails(movie);
            setCurrentDirectors(directors);
            setCurrentCast(cast);
            setTrailerUrl(trailer);
        };

        fetchMovieDetails();
    }, [currentMovieIndex]);

    const handleNext = () => {
        setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
    };

    const handlePrev = () => {
        setCurrentMovieIndex((prevIndex) =>
            prevIndex === 0 ? movies.length - 1 : prevIndex - 1
        );
    };

    const getMovieDetails = async (movie) => {
        const apiKey = "YOUR_YOUTUBE_API_KEY";
        const apiUrl = `https://www.googleapis.com/youtube/v3/search?part=snippet&q=${encodeURIComponent(movie.title)}+trailer&type=video&key=${apiKey}`;
        const response = await fetch(apiUrl);
        const data = await response.json();

        let trailer = "";
        if (data.items && data.items.length > 0) {
            trailer = `https://www.youtube.com/watch?v=${data.items[0].id.videoId}`;
        }

        const directors = []; // You can fetch directors from another source if needed
        const cast = []; // You can fetch cast from another source if needed

        return { directors, cast, trailer };
    };

    const currentMovie = movies[currentMovieIndex];

    return (
        <div className="movie-carousel">
            <div className="movie-details">
                <div className="poster-container">
                    <img
                        className="poster"
                        src={currentMovie.poster}
                        alt="Movie Poster"
                    />
                </div>
                <div className="details">
                    <h2>{currentMovie.title}</h2>
                    <h3>Directors:</h3>
                    <ul>
                        {currentDirectors.map((director, index) => (
                            <li key={index}>{director}</li>
                        ))}
                    </ul>
                    <h3>Cast:</h3>
                    <ul>
                        {currentCast.map((actor, index) => (
                            <li key={index}>
                                {actor.name} - {actor.character}
                            </li>
                        ))}
                    </ul>
                    <h3>Trailer:</h3>
                    <iframe
                        title="trailer"
                        width="560"
                        height="315"
                        src={trailerUrl}
                        frameBorder="0"
                        allowFullScreen
                    ></iframe>
                </div>
            </div>
            <div className="navigation-buttons">
                <button className="prev-btn" onClick={handlePrev}>
                    Previous
                </button>
                <button className="next-btn" onClick={handleNext}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default MovieCarousel;
