// MovieCarousel.js
import React, { useState } from "react";
import movies from "./moviesData";

const MovieCarousel = () => {


    const handleNext = () => {
        const sagaMovies =
            movies[currentMovieIndex.saga][currentMovieIndex.category];
        setCurrentMovieIndex((prevIndex) => {
            const newIndex = (prevIndex.index + 1) % sagaMovies.length;
            return { ...prevIndex, index: newIndex };
        });
    };

    const handlePrev = () => {
        const sagaMovies =
            movies[currentMovieIndex.saga][currentMovieIndex.category];
        setCurrentMovieIndex((prevIndex) => {
            const newIndex =
                prevIndex.index === 0
                    ? sagaMovies.length - 1
                    : prevIndex.index - 1;
            return { ...prevIndex, index: newIndex };
        });
    };

    const currentMovie =
        movies[currentMovieIndex.saga][currentMovieIndex.category][
            currentMovieIndex.index
        ];

    return (
        <div className="movie-carousel">
            <div className="movie-details">
                <div className="poster-container">
                    <img
                        className="poster"
                        src={currentMovie.poster}
                        alt="Movie Poster"
                    />
                </div>
                <div className="trailer-container">
                    <iframe
                        className="trailer"
                        width="560"
                        height="315"
                        src={`https://www.youtube.com/embed/${currentMovie.youtubeId}`}
                        title="YouTube video player"
                        allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                        allowFullScreen
                    ></iframe>
                </div>
                <div className="people-involved">
                    <h3>People Involved</h3>
                    <ul>
                        {currentMovie.people.map((person, index) => (
                            <li key={index}>{person}</li>
                        ))}
                    </ul>
                </div>
            </div>
            <div className="navigation-buttons">
                <button className="prev-btn" onClick={handlePrev}>
                    Previous
                </button>
                <button className="next-btn" onClick={handleNext}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default MovieCarousel;
