import React, { useState } from "react";
import "./Menus.css";

function Header({ setMovies }) {
    // Lista de categorías y opciones de idioma
    const categories = ["Drama", "Comedia", "Infantil", "Sagas"];
    const languageOptions = ["Español", "Inglés"];

    // Estados locales

    const [isOpen, setIsOpen] = useState(false); // Estado para controlar la apertura del menú
    const [selectedOption, setSelectedOption] = useState(categories[0]); // Opción seleccionada por defecto

    // Manejar la búsqueda de películas
    const handleSearch = () => {
        // Lógica para buscar películas y actualizar el estado de películas
    };

    // Manejar la selección de una opción
    const handleOptionSelect = (option) => {
        setSelectedOption(option);
        // Lógica para manejar la selección de la opción
    };

    return (
        <header className="menu">
            {/* Logo */}
            <img
                src="http://imgfz.com/i/OehyQAa.png"
                alt="Logo"
                className="logo"
            />

            {/* Menú de navegación */}
            <nav>
                {/* Opción de Inicio */}
                <a href="#">
                    Inicio <span></span>
                </a>

                {/* Dropdown de Categorías */}
                <div
                    className="dropdown"
                    onMouseEnter={() => setIsOpen(true)}
                    onMouseLeave={() => setIsOpen(false)}
                >
                    <a href="#">
                        Categorías <span></span>
                    </a>
                    {/* Lista de categorías */}
                    {isOpen && (
                        <ul>
                            {categories.map((option, index) => (
                                <li
                                    key={index}
                                    onClick={() => handleOptionSelect(option)}
                                >
                                    {option}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>

                {/* Dropdown de Idioma */}
                <div className="dropdown">
                    <a href="#">
                        Idioma <span></span>
                    </a>
                    {/* Lista de opciones de idioma */}
                    {isOpen && (
                        <ul>
                            {languageOptions.map((option, index) => (
                                <li
                                    key={index}
                                    onClick={() => handleOptionSelect(option)}
                                >
                                    {option}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </nav>
        </header>
    );
}

export default Header;
