import React, { useState } from 'react';
import { moviesBySagaAndCategory } from './moviesData';

function MovieDetails() {
  const [selectedSaga, setSelectedSaga] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleSagaChange = (event) => {
    setSelectedSaga(event.target.value);
    setSelectedCategory(null); // Reset category when saga changes
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
    setSelectedSaga(null); // Reset saga when category changes
  };

  const getFilteredMovies = () => {
    if (selectedSaga) {
      return moviesBySagaAndCategory[selectedSaga];
    } else if (selectedCategory) {
      const movies = {};
      Object.keys(moviesBySagaAndCategory).forEach(saga => {
        if (moviesBySagaAndCategory[saga][selectedCategory]) {
          movies[saga] = moviesBySagaAndCategory[saga][selectedCategory];
        }
      });
      return movies;
    } else {
      return moviesBySagaAndCategory;
    }
  };

  const renderMovies = (movies) => {
    return Object.keys(movies).map(saga => (
      <div key={saga}>
        <h3>{saga}</h3>
        {movies[saga].map(movie => (
          <div key={movie.title} className="movie">
            <h4>{movie.title}</h4>
            <img src={movie.poster} alt={movie.title} />
            <p>Rating: {movie.rating}</p>
            <p>Description: {movie.description}</p>
            {/* Aquí podrías agregar más detalles como reseñas */}
          </div>
        ))}
      </div>
    ));
  };

  return (
    <div className="movie-details">
      <h2>Movie Details</h2>
      <div>
        <label htmlFor="sagas">Saga:</label>
        <select id="sagas" onChange={handleSagaChange} value={selectedSaga || ''}>
          <option value="">All Sagas</option>
          {Object.keys(moviesBySagaAndCategory).map(saga => (
            <option key={saga} value={saga}>{saga}</option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="categories">Category:</label>
        <select id="categories" onChange={handleCategoryChange} value={selectedCategory || ''}>
          <option value="">All Categories</option>
          {Object.keys(moviesBySagaAndCategory).reduce((categories, saga) => {
            Object.keys(moviesBySagaAndCategory[saga]).forEach(category => {
              if (!categories.includes(category)) {
                categories.push(category);
              }
            });
            return categories;
          }, []).map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>
      </div>
      <div className="movies">
        {renderMovies(getFilteredMovies())}
      </div>
    </div>
  );
}

export default MovieDetails;
