import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
    const API_URL = "https://api.themoviedb.org/3";
    const API_KEY = "674684d28cd5c404ad1bf06cd1a5d482";

    const [genres, setGenres] = useState([]);
    const [languages, setLanguages] = useState([]);

    const fetchGenres = async () => {
        const { data } = await axios.get(`${API_URL}/genre/movie/list`, {
            params: {
                api_key: API_KEY,
            },
        });
        setGenres(data.genres);
    };

    const fetchLanguages = async () => {
        const { data } = await axios.get(`${API_URL}/configuration/languages`, {
            params: {
                api_key: API_KEY,
            },
        });
        setLanguages(data);
    };

    useEffect(() => {
        fetchGenres();
        fetchLanguages();
    }, []);

    return (
        <div className="App">
            {/* Sección para mostrar las categorías */}
            <div className="container mt-3">
                <h2>Categorías</h2>
                <div>
                    {genres.map((genre) => (
                        <div key={genre.id}>{genre.name}</div>
                    ))}
                </div>
            </div>

            {/* Sección para mostrar los idiomas */}
            <div className="container mt-3">
                <h2>Idiomas</h2>
                <div>
                    {languages.map((language, index) => (
                        <div key={index}>{language.english_name}</div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default App;
