import React, { useState } from 'react';

function MovieDetails({ moviesBySagaAndCategory }) {
  const [selectedSaga, setSelectedSaga] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleSagaChange = (saga) => {
    setSelectedSaga(saga);
    setSelectedCategory(null); // Reset category selection when changing saga
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
    setSelectedSaga(null); // Reset saga selection when changing category
  };

  return (
    <div className="movie-details">
      <div>
        <h2>Filter by Saga:</h2>
        {Object.keys(moviesBySagaAndCategory).map((saga, index) => (
          <button key={index} onClick={() => handleSagaChange(saga)}>
            {saga}
          </button>
        ))}
      </div>
      <div>
        <h2>Filter by Category:</h2>
        {selectedSaga &&
          Object.keys(moviesBySagaAndCategory[selectedSaga]).map((category, index) => (
            <button key={index} onClick={() => handleCategoryChange(category)}>
              {category}
            </button>
          ))}
      </div>
      <h2>Movie Details</h2>
      {/* Aquí van los detalles de la película seleccionada */}
    </div>
  );
}

export default MovieDetails;
