import React, { useState } from 'react';
import { moviesBySagaAndCategory } from './moviesData';

function MovieDetails() {
  const [selectedSaga, setSelectedSaga] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleSagaChange = (event) => {
    setSelectedSaga(event.target.value);
    setSelectedCategory(null); // Reset category when saga changes
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const getSagas = () => {
    return Object.keys(moviesBySagaAndCategory);
  };

  const getCategories = () => {
    if (selectedSaga && moviesBySagaAndCategory[selectedSaga]) {
      return Object.keys(moviesBySagaAndCategory[selectedSaga]);
    }
    return [];
  };

  const getPosters = () => {
    if (selectedSaga && selectedCategory && moviesBySagaAndCategory[selectedSaga][selectedCategory]) {
      return moviesBySagaAndCategory[selectedSaga][selectedCategory].map(movie => (
        <img key={movie.title} src={movie.poster} alt={movie.title} />
      ));
    }
    return null;
  };

  return (
    <div className="movie-details">
      <h2>Movie Details</h2>
      <div>
        <label htmlFor="sagas">Saga:</label>
        <select id="sagas" onChange={handleSagaChange} value={selectedSaga || ''}>
          <option value="">Select Saga</option>
          {getSagas().map(saga => (
            <option key={saga} value={saga}>{saga}</option>
          ))}
        </select>
      </div>
      <div>
        <label htmlFor="categories">Category:</label>
        <select id="categories" onChange={handleCategoryChange} value={selectedCategory || ''}>
          <option value="">Select Category</option>
          {getCategories().map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>
      </div>
      <div className="posters">
        {getPosters()}
      </div>
    </div>
  );
}

export default MovieDetails;
