import React from 'react';

const extractYoutubeId = (url) => {
  const regExp =
      /^.*(?:youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|\&v=)([^#\&\?]*).*/;
  const match = url.match(regExp);
  return match && match[1].length === 11 ? match[1] : null;
};

const Movie = ({ title, poster, youtubeLink, people }) => {
  const youtubeId = extractYoutubeId(youtubeLink);

  return (
    <div className="movie">
      <img src={poster} alt={title} />
      <h3>{title}</h3>
      <p>Category: {people.join(', ')}</p>
      <p>Youtube ID: {youtubeId}</p>
    </div>
  );
};

const MovieList = ({ movies }) => {
  return (
    <div className="movie-list">
      {movies.map((movie, index) => (
        <Movie key={index} {...movie} />
      ))}
    </div>
  );
};

const App = () => {
  const movies = [
    {
        title: "Star Wars Episode I",
        poster: "https://posters.movieposterdb.com/12_10/1999/120915/l_120915_9275ff24.jpg",
        youtubeLink: "https://www.youtube.com/watch?v=bD7bpG-zDJQ",
        people: ["Persona 1", "Persona 2", "Persona 3"],
    },
    {
        title: "Pelicula 2",
        poster: "url_al_poster_2",
        youtubeLink: "https://www.youtube.com/watch?v=UzFZR2dRsSY&t=49s",
        people: ["Persona 4", "Persona 5", "Persona 6"],
    },
  ];

  return (
    <div className="app">
      <h1>Página web de reseña de películas</h1>
      <MovieList movies={movies} />
    </div>
  );
};

export default App;
