// moviesData.js
const extractYoutubeId = (url) => {
  const regExp = /^.*(?:youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|\&v=)([^#\&\?]*).*/;
  const match = url.match(regExp);
  return match && match[1].length === 11 ? match[1] : null;
};

const fetchYoutubeVideoDetails = async (youtubeId) => {
  try {
    const response = await fetch(`https://www.googleapis.com/youtube/v3/videos?id=${youtubeId}&part=snippet&key=YOUR_API_KEY`);
    const data = await response.json();
    return data.items[0].snippet;
  } catch (error) {
    console.error('Error fetching YouTube video details:', error);
    return null;
  }
};

const movies = [
  {
    title: "Pelicula 1",
    youtubeLink: "https://www.youtube.com/watch?v=qIYyXcCwvKc",
    people: []
  },
  {
    title: "Pelicula 2",
    youtubeLink: "https://www.youtube.com/watch?v=abcdefghijk",
    people: []
  },
  // Añade más películas si lo deseas
];

const fillMovieDetails = async () => {
  for (const movie of movies) {
    const youtubeId = extractYoutubeId(movie.youtubeLink);
    if (youtubeId) {
      const videoDetails = await fetchYoutubeVideoDetails(youtubeId);
      if (videoDetails) {
        movie.title = videoDetails.title;
        movie.poster = videoDetails.thumbnails.default.url;
      }
    }
  }
};

fillMovieDetails();

export default movies;
