// MovieDetails.js
import React from 'react';

function MovieDetails({ movie }) {
  return (
    <div className="movie-details">
      <h2>{movie.title}</h2>
      <p>Rating: {movie.rating}</p>
      <p>Category: {movie.category}</p>
      <p>Saga: {movie.saga}</p>
      {/* Aquí va la lista de reseñas */}
    </div>
  );
}

export default MovieDetails;
