import React, { useState, useEffect } from 'react';
import './App.css'; // Archivo de estilos CSS

const App = () => {
  const [movies, setMovies] = useState([]);
  const [currentMovieIndex, setCurrentMovieIndex] = useState(0);

  useEffect(() => {
    // Cargar datos de películas desde el archivo JSON
    fetch('movies.json')
      .then(response => response.json())
      .then(data => setMovies(data))
      .catch(error => console.error('Error al cargar las películas:', error));
  }, []);

  const handleNext = () => {
    setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
  };

  const handlePrev = () => {
    setCurrentMovieIndex((prevIndex) =>
      prevIndex === 0 ? movies.length - 1 : prevIndex - 1
    );
  };

  const currentMovie = movies[currentMovieIndex];
  const youtubeUrl = `https://www.youtube.com/embed/${currentMovie.youtubeId}`;

  return (
    <div className="app">
      {movies.length > 0 && (
        <div className="movie-carousel">
          <div className="movie-details">
            <img className="poster" src={currentMovie.poster} alt="Movie Poster" />
            <div className="people-involved">
              <h3>People Involved</h3>
              <ul>
                {currentMovie.people.map((person, index) => (
                  <li key={index}>{person}</li>
                ))}
              </ul>
            </div>
          </div>
          <div className="trailer-container">
            <iframe
              className="trailer"
              width="560"
              height="315"
              src={youtubeUrl}
              title="YouTube video player"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
              referrerPolicy="strict-origin-when-cross-origin"
              allowFullScreen
            ></iframe>
          </div>
          <button className="prev-btn" onClick={handlePrev}>Previous</button>
          <button className="next-btn" onClick={handleNext}>Next</button>
        </div>
      )}
    </div>
  );
};

export default App;
