import React from 'react';
import Slider from 'react-slick';
import "./MovieCarousel.css";
import "./MovieCarousel.css";

function MovieCarousel({ movies, selectMovie }) {
    const settings = {
        dots: true,
        infinite: true,
        speed: 300,
        slidesToShow: 5,
        slidesToScroll: 5,
        responsive: [
            
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: true,
                    dots: true
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };

    return (
        <div style={{ maxWidth: '1300px', margin: '40px auto' }}>
            <Slider {...settings}>
                {movies.map(movie => (
                    <div key={movie.id} onClick={() => selectMovie(movie)} style={{ padding: "15px", textAlign: 'center' }}>
                        <img src={`https://image.tmdb.org/t/p/w200${movie.poster_path}`} alt={movie.title} style={{ maxWidth: "400px", height: "auto" }} />
                    </div>
                ))}
            </Slider>
        </div>
    );
}

export default MovieCarousel;
