// moviesData.js
const MOVIEPOSTERDB_API_KEY = "234|tS5SPZa4ikob6q5ExKK86NRdvLIqAQLWxj93bFxZ";

const fetchMovieDetails = async (movieId) => {
    try {
        const response = await fetch(
            `https://api.movieposterdb.com/movie/${movieId}?api_key=${MOVIEPOSTERDB_API_KEY}`
        );
        const data = await response.json();
        return data;
    } catch (error) {
        console.error("Error fetching movie details:", error);
        return null;
    }
};

const movies = [
    {
        movieId: "1", // Ejemplo de ID de película en MoviePosterDB
        people: [],
    },
    {
        movieId: "67890", // Ejemplo de ID de película en MoviePosterDB
        people: [],
    },
    // Agrega más películas con sus respectivos IDs de MoviePosterDB
];

const fillMovieDetails = async () => {
    for (const movie of movies) {
        const movieDetails = await fetchMovieDetails(movie.movieId);
        if (movieDetails) {
            movie.title = movieDetails.title;
            movie.poster = movieDetails.poster_url;
            // Se puede obtener más información, como el reparto, desde movieDetails.cast
        }
    }
};

fillMovieDetails();

export default movies;
