// moviesData.js
const OMDB_API_KEY = "b7866dbd";

const fetchMovieDetails = async (title) => {
    try {
        const response = await fetch(
            `http://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`
        );
        const data = await response.json();
        return data;
    } catch (error) {
        console.error("Error fetching movie details:", error);
        return null;
    }
};

const movies = [
    {
        title: "Star Wars",
        people: [],
    },
    {
        title: "The Godfather",
        people: [],
    },
    // Agrega más películas con sus respectivos títulos
];

const fillMovieDetails = async () => {
    for (const movie of movies) {
        const movieDetails = await fetchMovieDetails(movie.title);
        if (movieDetails) {
            movie.poster = movieDetails.Poster;
            // Se puede obtener más información, como el reparto, desde movieDetails.Actors
        }
    }
};

fillMovieDetails();

export default movies;
