import React from 'react';

const StarRating = ({ rating }) => {
    const stars = [];
    const scaledRating = (rating / 2);  // Convierte la calificación de una escala de 10 a una de 5
    const fullStars = Math.floor(scaledRating);
    const halfStar = scaledRating % 1 >= 0.5;

    for (let i = 0; i < fullStars; i++) {
        stars.push(<span key={i}>&#9733;</span>); // Estrella llena
    }
    if (halfStar) {
        stars.push(<span key={fullStars}>&#9734;</span>); // Media estrella
    }
    // Rellenar con estrellas vacías hasta llegar a 5
    while (stars.length < 5) {
        stars.push(<span key={stars.length}>&#9734;</span>); // Estrella vacía
    }
    return <div className="star-rating">{stars}</div>;
};

export default StarRating;
