// moviesData.js

const extractYoutubeId = (url) => {
    const regExp =
        /^.*(?:youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return match && match[1].length === 11 ? match[1] : null;
};

const movies = [
    {
        title: "Star Wars Episode I",
        poster: "https://posters.movieposterdb.com/12_10/1999/120915/l_120915_9275ff24.jpg",
        youtubeLink: "https://www.youtube.com/watch?v=bD7bpG-zDJQ",
        people: ["Persona 1", "Persona 2", "Persona 3"],
        rating: "9.5",
        saga: "Star Wars"
    },

    {
        title: "Pelicula 2",
        poster: "url_al_poster_2",
        youtubeLink: "https://www.youtube.com/watch?v=UzFZR2dRsSY&t=49s",
        people: ["Persona 4", "Persona 5", "Persona 6"],
        rating: "9.5",
        saga: "Marvel"
    },

    // Agrega más películas aquí
];

movies.forEach((movie) => {
    movie.youtubeId = extractYoutubeId(movie.youtubeLink);
});

// Organizar películas por sagas y categorías
const organizedMovies = {};

movies.forEach((movie) => {
    if (!organizedMovies[movie.saga]) {
        organizedMovies[movie.saga] = {};
    }

    // Agrupar por categorías dentro de cada saga
    if (!organizedMovies[movie.saga][movie.category]) {
        organizedMovies[movie.saga][movie.category] = [];
    }

    organizedMovies[movie.saga][movie.category].push(movie);
});

export default organizedMovies;
