const fetch = require("node-fetch");

const extractYoutubeId = (url) => {
    const regExp =
        /^.*(?:youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);
    return match && match[1].length === 11 ? match[1] : null;
};

const API_KEY = "TU_CLAVE_DE_API";

const fetchPoster = async (title) => {
    try {
        const response = await fetch(
            `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=${encodeURIComponent(
                title
            )}`
        );
        const data = await response.json();
        if (data.results.length > 0) {
            return `https://image.tmdb.org/t/p/w500${data.results[0].poster_path}`;
        } else {
            console.error(`No se encontró el poster para ${title}`);
            return null;
        }
    } catch (error) {
        console.error("Error al obtener el poster:", error);
        return null;
    }
};

const movies = [
    {
        title: "Star Wars Episode I",
        youtubeLink: "https://www.youtube.com/watch?v=bD7bpG-zDJQ",
        people: ["Liam Neeson", "Ewan McGregor", "Natalie Portman"],
        rating: "6.5",
        saga: "Star Wars",
        category: "Science Fiction",
    },
    {
        title: "Avengers: Infinity War",
        youtubeLink: "https://www.youtube.com/watch?v=6ZfuNTqbHE8",
        people: ["Robert Downey Jr.", "Chris Hemsworth", "Mark Ruffalo"],
        rating: "8.4",
        saga: "Marvel",
        category: "Super",
    },
];

// Llamada a fetchPoster para obtener los posters de las películas
const fetchPosters = async () => {
    for (const movie of movies) {
        movie.poster = await fetchPoster(movie.title);
    }
};

fetchPosters();

const moviesBySagaAndCategory = movies.reduce((acc, movie) => {
    const { saga, category } = movie;
    if (!acc[saga]) {
        acc[saga] = {};
    }
    if (!acc[saga][category]) {
        acc[saga][category] = [];
    }
    acc[saga][category].push(movie);
    return acc;
}, {});

movies.forEach((movie) => {
    movie.youtubeId = extractYoutubeId(movie.youtubeLink);
});

export { moviesBySagaAndCategory };
export default movies;
