// Favorites.js
import React from 'react';
import { URL_IMAGE } from './constants'; // Asegúrate de tener las constantes URL definidas en un archivo separado si no están en App.js

const Favorites = ({ favorites, selectMovie }) => {
    return (
        <div className="container_movies">
            <h2>Favorites</h2>
            <div className="row_movies">
                {favorites.map((movie) => (
                    <div
                        key={movie.id}
                        className="col_movies"
                        onClick={() => selectMovie(movie)}
                    >
                        <img src={`${URL_IMAGE + movie.poster_path}`} alt={movie.title} />
                        <h4>{movie.title}</h4>
                        <p>Rating: {movie.vote_average}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Favorites;
