import React, { useState } from 'react';
import './App.css'; // Archivo de estilos CSS

const movies = [
  {
    title: 'Pelicula 1',
    poster: 'url_al_poster_1',
    youtubeId: 'ID_DE_YOUTUBE_1',
    people: ['Persona 1', 'Persona 2', 'Persona 3'],
  },
  {
    title: 'Pelicula 2',
    poster: 'url_al_poster_2',
    youtubeId: 'ID_DE_YOUTUBE_2',
    people: ['Persona 4', 'Persona 5', 'Persona 6'],
  },
  // Añade más películas si lo deseas
];

const App = () => {
  const [currentMovieIndex, setCurrentMovieIndex] = useState(0);

  const handleNext = () => {
    setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % movies.length);
  };

  const handlePrev = () => {
    setCurrentMovieIndex((prevIndex) =>
      prevIndex === 0 ? movies.length - 1 : prevIndex - 1
    );
  };

  const currentMovie = movies[currentMovieIndex];

  const embedUrl = `https://www.youtube.com/watch?v=qIYyXcCwvKc`;

  return (
    <div className="app">
      <div className="movie-carousel">
        <div className="movie-details">
          <img
            className="poster"
            src={currentMovie.poster}
            alt="Movie Poster"
          />
          <div className="people-involved">
            <h3>People Involved</h3>
            <ul>
              {currentMovie.people.map((person, index) => (
                <li key={index}>{person}</li>
              ))}
            </ul>
          </div>
        </div>
        <div className="trailer-container">
          <iframe
            className="trailer"
            width="560"
            height="315"
            src={embedUrl}
            title="YouTube Video Player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        </div>
        <button className="prev-btn" onClick={handlePrev}>
          Previous
        </button>
        <button className="next-btn" onClick={handleNext}>
          Next
        </button>
      </div>
    </div>
  );
};

export default App;
