import React from 'react';
import './StarRating.css';

const StarRating = ({ rating }) => {
    const stars = [];
    const scaledRating = (rating / 2.5);  // Convierte la calificación de una escala de 10 a una de 5
    const fullStars = Math.floor(scaledRating);
    const halfStar = scaledRating % 1 >= 0.5;

    // Asegúrate de que siempre haya exactamente 5 estrellas
    for (let i = 0; i < 5; i++) {
        if (i < fullStars) {
            stars.push(<span key={i} className="star">&#9733;</span>); // Estrella llena
        } else if (i === fullStars && halfStar) {
            stars.push(<span key={i} className="star">&#9733;&#9734;</span>); // Media estrella
        } else {
            stars.push(<span key={i} className="star">&#9734;</span>); // Estrella vacía
        }
    }

    return <div className="star-rating">{stars}</div>;
};

export default StarRating;
